import React, { useCallback, useRef } from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const FondoParticulas = ({ triggerAnimation }) => {
  const particlesRef = useRef(null);

  const particlesInit = useCallback(async (main) => {
    await loadFull(main);
  }, []);

  const particlesLoaded = useCallback(async (container) => {
    particlesRef.current = container;
  }, []);

  // ðŸš€ Efecto al interactuar con el reproductor
  React.useEffect(() => {
    if (triggerAnimation && particlesRef.current) {
      const particles = particlesRef.current;

      // Sube velocidad al interactuar
      particles.options.particles.move.speed = 3;

      // Vuelve lento despuÃ©s de 2s
      setTimeout(() => {
        particles.options.particles.move.speed = 0.5;
      }, 2000);
    }
  }, [triggerAnimation]);

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      loaded={particlesLoaded}
      options={{
        fullScreen: { enable: true },
        background: {
          color: { value: "transparent" },
        },
        fpsLimit: 60,
        interactivity: {
          events: {
            onHover: { enable: true, mode: "slow" }, // ðŸŒŠ mÃ¡s fluido
            onClick: { enable: true, mode: "push" },
          },
          modes: {
            slow: { factor: 2, radius: 200 }, // hover suave
            push: { quantity: 2 },            // clic agrega pocas partÃ­culas
          },
        },
        particles: {
          color: { value: ["#26dc6c", "#1c39b9", "#ffffff"] },
          links: {
            enable: true,
            color: "#ffffff",
            distance: 120,
            opacity: 0.3,
            width: 1,
          },
          move: {
            enable: true,
            speed: 0.5, // ðŸŒ™ lento por defecto
            outModes: { default: "bounce" },
          },
          number: { value: 120, density: { enable: true, area: 600 } },
          opacity: {
            value: 0.6,
            animation: { enable: true, speed: 0.5, sync: false },
          },
          shape: { type: ["circle", "triangle", "square"] },
          size: {
            value: { min: 2, max: 6 },
            animation: { enable: true, speed: 1, sync: false },
          },
        },
        detectRetina: true,
      }}
    />
  );
};

export default FondoParticulas;
