// reproductor.js
import React, { useContext, useEffect, useState } from "react";
import { Play, Pause, Volume2, VolumeX } from "lucide-react";
import { AudioContextGlobal } from "../contexts/AudioContext";
import FondoParticulas from "../components/fondoparticulas"; // Asegúrate que la ruta sea correcta
import './reproductor.css';

const Reproductor = () => {
  const { isPlaying, togglePlay, volume, setVolume, toggleMute, streamUrl } = useContext(AudioContextGlobal);
  const [isMuted, setIsMuted] = useState(volume === 0);
  const [currentTime, setCurrentTime] = useState(
    new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })
  );

  useEffect(() => setIsMuted(volume === 0), [volume]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleVolumeChange = (e) => setVolume(Number(e.target.value));
  const handleMute = () => toggleMute();

  return (
    <div className="reproductor-page">
      {/* Fondo de partículas */}
      <FondoParticulas />

      {/* Contenido principal */}
      <div className="reproductor-content">
        <img src="/images/logo.png" alt="Radio Oriente" className="reproductor-logo" />

        <div className="reproductor-controls">
          <button onClick={togglePlay} className="btn-play">
            {isPlaying ? <Pause size={50}/> : <Play size={50}/>}
          </button>
        </div>

        <div className="reproductor-volume">
          <button onClick={handleMute} className="btn-volume">
            {isMuted ? <VolumeX size={30}/> : <Volume2 size={30}/>}
          </button>
          <input
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={handleVolumeChange}
            className="volume-slider"
          />
        </div>

        <span className="reproductor-time">{currentTime}</span>
        <span className="reproductor-status">{streamUrl ? "En vivo" : "Cargando stream..."}</span>
      </div>
    </div>
  );
};

export default Reproductor;
