// src/components/RadioPlayer.js
import React, { useRef, useState, useEffect, useContext } from "react";
import { Play, Pause, Volume2, VolumeX, ChevronDown, ChevronUp } from "lucide-react";
import './RadioPlayer.css';
import { AudioContextGlobal } from "../contexts/AudioContext";

const RadioPlayer = () => {
  const { audioRef: globalAudioRef, isPlaying: globalIsPlaying, setIsPlaying: setGlobalIsPlaying, volume: globalVolume, setVolume: setGlobalVolume } = useContext(AudioContextGlobal);

  const [isPlaying, setIsPlaying] = useState(globalIsPlaying);
  const [volume, setVolume] = useState(globalVolume);
  const [lastVolume, setLastVolume] = useState(volume);
  const [isMuted, setIsMuted] = useState(volume === 0);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [streamUrl, setStreamUrl] = useState(null);

  // Inicializar audio global si no existe
  useEffect(() => {
    fetch("/api/radio/station/")
      .then(res => res.json())
      .then(data => {
        setStreamUrl(data.stream_url);
        if (!globalAudioRef.current) {
          globalAudioRef.current = new Audio(data.stream_url);
          globalAudioRef.current.volume = globalVolume;
        }
      })
      .catch(err => console.error("Error fetching stream URL:", err));
  }, []);

  // Mantener volumen sincronizado con contexto
  useEffect(() => {
    if (globalAudioRef.current) {
      globalAudioRef.current.volume = volume;
    }
    setIsMuted(volume === 0);
    setGlobalVolume(volume);
    localStorage.setItem("radioVolume", volume);
  }, [volume]);

  // Actualizar tiempo
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const togglePlay = () => {
    if (!globalAudioRef.current) return;
    if (isPlaying) {
      globalAudioRef.current.pause();
      setIsPlaying(false);
      setGlobalIsPlaying(false);
    } else {
      globalAudioRef.current.play().catch(console.error);
      setIsPlaying(true);
      setGlobalIsPlaying(true);
    }
  };

  const handleVolumeChange = (e) => {
    const newVol = Number(e.target.value);
    setVolume(newVol);
    if (newVol > 0) setLastVolume(newVol);
  };

  const toggleMute = () => {
    if (isMuted) {
      setVolume(lastVolume || 1);
    } else {
      setLastVolume(volume);
      setVolume(0);
    }
  };

  const toggleCollapse = () => setIsCollapsed(prev => !prev);

  return (
    <div className={`radio-player-wrapper ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="collapse-btn-wrapper">
        <button 
          className="collapse-btn" 
          onClick={toggleCollapse}
          aria-label={isCollapsed ? "Mostrar reproductor" : "Ocultar reproductor"}
        >
          {isCollapsed ? <ChevronUp size={20}/> : <ChevronDown size={20}/> }
        </button>
      </div>

      <div className={`radio-player ${isPlaying ? 'playing' : 'paused'}`}>
        <div className="gradient-left" />
        <div className="gradient-right" />

        <div className="radio-info">
          <span
            className={`status-indicator ${isPlaying ? 'playing' : 'paused'}`}
            aria-label={isPlaying ? "En transmisi칩n" : "No est치 transmitiendo"}
            title={isPlaying ? "On Air" : "Off Air"}
          />
          <div className="radio-details">
            <span className="radio-name">Radio Oriente FM</span>
            <span className="radio-frequency">107.1 FM - En vivo</span>
          </div>
        </div>     

        <div className="player-controls">
          <button
            onClick={togglePlay}
            aria-label="Play/Pause"
            className="control-icon"
            disabled={!streamUrl}
          >
            {isPlaying ? <Pause size={20} /> : <Play size={20} />}
          </button>
          <span className="text-sm mt-1 opacity-75">{currentTime}</span>
        </div>

        <div className="volume-controls">
          <button
            onClick={toggleMute}
            aria-label="Mute/Unmute"
            className="control-button"
          >
            {isMuted ? (
              <VolumeX size={20} className="volume-icon" />
            ) : (
              <Volume2 size={20} className="volume-icon" />
            )}
          </button>

          <div className="volume-slider-container">
            <div 
              className={`volume-progress ${isPlaying ? 'playing' : ''}`}
              style={{ width: `${volume * 100}%` }}
            />
            <div 
              className="volume-thumb"
              style={{ left: `calc(${volume * 100}% - 7px)` }}
            />
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={handleVolumeChange}
              className="volume-range"
              aria-label="Volume control"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default RadioPlayer;
