import React from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";
import "./fondoparticulas.css"; // Importa el CSS del gradiente

const FondoParticulas = () => {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        fullScreen: { enable: true },
        fpsLimit: 60,
        interactivity: {
          events: {
            onHover: { enable: true, mode: ["attract", "bubble"] },
            onClick: { enable: true, mode: "push" }
          },
          modes: {
            attract: { distance: 200, duration: 0.4, factor: 3 },
            bubble: { distance: 250, size: 8, duration: 2, opacity: 0.8 },
            push: { quantity: 3 }
          }
        },
        particles: {
          color: {
            value: ["#26dc6c", "#1c39b9", "#991b1b", "#7f1d1d", "#ffffff"]
          },
          links: {
            enable: true,
            color: "#ffffff",
            distance: 150,
            opacity: 0.6,
            width: 1
          },
          collisions: { enable: true },
          move: {
            enable: true,
            speed: 2,
            random: true,
            straight: false,
            outModes: { default: "bounce" }
          },
          number: {
            value: 150,
            density: { enable: true, area: 600 }
          },
          opacity: {
            value: 0.7,
            random: { enable: true, minimumValue: 0.3 },
            animation: {
              enable: true,
              speed: 1,
              minimumValue: 0.1,
              sync: false
            }
          },
          shape: {
            type: ["circle", "triangle", "square", "star", "polygon", "char"],
            options: {
              char: [
                { value: "â­", font: "Verdana", style: "", weight: "400" },
                { value: "ðŸŽ¶", font: "Verdana", style: "", weight: "400" },
                { value: "âš½", font: "Verdana", style: "", weight: "400" }
              ]
            }
          },
          size: {
            value: 4,
            random: { enable: true, minimumValue: 2 },
            animation: {
              enable: true,
              speed: 2,
              minimumValue: 1,
              sync: false
            }
          }
        },
        detectRetina: true,
        emitters: {
          direction: "none",
          life: { count: 0, duration: 0.1, delay: 0.1 },
          rate: { delay: 0.5, quantity: 1 },
          size: { width: 0, height: 0 },
          position: { x: 50, y: 50 }
        }
      }}
    />
  );
};

export default FondoParticulas;
