import { useLocation } from "react-router-dom";

function App() {
  const location = useLocation();
  const hideLayout = location.pathname === "/reproductor";

  return (
    <>
      {!hideLayout && <Navbar />}
      <Routes>
        <Route path="/reproductor" element={<Reproductor />} />
        {/* dem√°s rutas */}
      </Routes>
      {!hideLayout && <Footer />}
    </>
  );
}
