import React from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const FondoParticulas = () => {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        fullScreen: { enable: true },
        background: { color: { value: "#0A1F2B" } },
        fpsLimit: 60,
        interactivity: {
          events: {
            onHover: {
              enable: true,
              mode: "repulse"
            }
          },
          modes: {
            repulse: {
              distance: 100,
              duration: 0.4
            }
          }
        },
        particles: {
          color: {
            value: "#dc2626"
          },
          links: {
            enable: true,
            color: "#ffffff",
            distance: 120,
            opacity: 0.5
          },
          collisions: {
            enable: false
          },
          move: {
            enable: true,
            speed: 1,
            direction: "none",
            random: false,
            straight: false,
            outModes: {
              default: "bounce"
            }
          },
          number: {
            value: 60,
            density: {
              enable: true,
              area: 800
            }
          },
          opacity: {
            value: 0.5
          },
          shape: {
            type: "circle"
          },
          size: {
            value: 3
          }
        },
        detectRetina: true
      }}
    />
  );
};

export default FondoParticulas;
