import React, { useRef, useState, useEffect } from "react";
import { Play, Pause, Volume2, VolumeX, ChevronDown, ChevronUp } from "lucide-react";
import './RadioPlayer.css';

const RadioPlayer = () => {
  const audioRef = useRef(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(() => {
    const saved = localStorage.getItem("radioVolume");
    return saved !== null ? Number(saved) : 1;
  });
  const [lastVolume, setLastVolume] = useState(volume);
  const [isMuted, setIsMuted] = useState(volume === 0);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [streamUrl, setStreamUrl] = useState(null);

  // Inicializar audio solo una vez
  useEffect(() => {
    if (!streamUrl) return;

    if (!audioRef.current) {
      audioRef.current = new Audio(streamUrl);
      audioRef.current.volume = volume;
    } else {
      audioRef.current.src = streamUrl; // Si cambia la URL
    }
  }, [streamUrl]);

  // Traer URL desde backend
  useEffect(() => {
    fetch("/api/radio/station/") // Endpoint que devuelve JSON con stream_url
      .then(res => res.json())
      .then(data => setStreamUrl(data.stream_url))
      .catch(err => console.error("Error fetching stream URL:", err));
  }, []);

  // Actualizar hora cada segundo
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Actualizar volumen y mute
  useEffect(() => {
    if (audioRef.current) audioRef.current.volume = volume;
    setIsMuted(volume === 0);
    localStorage.setItem("radioVolume", volume);
  }, [volume]);

  // Play/Pause
  useEffect(() => {
    if (!audioRef.current) return;
    if (isPlaying) {
      audioRef.current.play().catch(err => console.error(err));
    } else {
      audioRef.current.pause();
    }
  }, [isPlaying]);

  const togglePlay = () => {
    if (!audioRef.current) return;
    setIsPlaying(prev => !prev);
  };

  const handleVolumeChange = (e) => {
    const newVol = Number(e.target.value);
    setVolume(newVol);
    if (newVol > 0) setLastVolume(newVol);
  };

  const toggleMute = () => {
    if (isMuted) {
      setVolume(lastVolume || 1);
    } else {
      setLastVolume(volume);
      setVolume(0);
    }
  };

  const toggleCollapse = () => setIsCollapsed(prev => !prev);

  return (
    <div className={`radio-player-wrapper ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="collapse-btn-wrapper">
        <button onClick={toggleCollapse} className="collapse-btn" aria-label="Collapse/Expand">
          {isCollapsed ? <ChevronUp size={20}/> : <ChevronDown size={20}/>}
        </button>
      </div>

      <div className={`radio-player ${isPlaying ? 'playing' : 'paused'}`}>
        <div className="gradient-left" />
        <div className="gradient-right" />

        <div className="radio-info">
          <span className={`status-indicator ${isPlaying ? 'playing' : 'paused'}`} />
          <div className="radio-details">
            <span className="radio-name">Radio Oriente FM</span>
            <span className="radio-frequency">107.1 FM - En vivo</span>
          </div>
        </div>

        <div className="player-controls">
          <button onClick={togglePlay} className="control-icon" disabled={!streamUrl}>
            {isPlaying ? <Pause size={20}/> : <Play size={20}/>}
          </button>
          <span className="text-sm mt-1 opacity-75">{currentTime}</span>
        </div>

        <div className="volume-controls">
          <button onClick={toggleMute} className="control-button">
            {isMuted ? <VolumeX size={20}/> : <Volume2 size={20}/>}
          </button>
          <div className="volume-slider-container">
            <div className="volume-progress" style={{width: `${volume*100}%`}} />
            <div className="volume-thumb" style={{left: `calc(${volume*100}% - 7px)`}} />
            <input type="range" min="0" max="1" step="0.01" value={volume} onChange={handleVolumeChange}/>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RadioPlayer;
