import React, { useState } from 'react';
import { Upload, Music, Users, Mail, Phone, MapPin, MessageSquare, Link, Send } from 'lucide-react';

const Emergente = () => {
  const [formData, setFormData] = useState({
    nombre_banda: '',
    integrantes: '',
    genero: '',
    ciudad: '',
    correo_contacto: '',
    telefono_contacto: '',
    mensaje: '',
    links: '',
    press_kit: null
  });

  const [isLoading, setIsLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormData(prev => ({
      ...prev,
      press_kit: file
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.nombre_banda.trim()) {
      newErrors.nombre_banda = 'El nombre de la banda es requerido';
    }
    
    if (!formData.genero.trim()) {
      newErrors.genero = 'El género musical es requerido';
    }
    
    if (!formData.correo_contacto.trim()) {
      newErrors.correo_contacto = 'El correo electrónico es requerido';
    } else if (!/\S+@\S+\.\S+/.test(formData.correo_contacto)) {
      newErrors.correo_contacto = 'Por favor ingresa un correo electrónico válido';
    }
    
    if (!formData.mensaje.trim()) {
      newErrors.mensaje = 'El mensaje es requerido';
    }

    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const newErrors = validateForm();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsLoading(true);
    
    try {
      const formDataToSend = new FormData();
      
      Object.keys(formData).forEach(key => {
        if (formData[key] !== null && formData[key] !== '') {
          formDataToSend.append(key, formData[key]);
        }
      });

      const response = await fetch('/api/emergentes/', {
        method: 'POST',
        body: formDataToSend,
      });
      
      if (!response.ok) {
        throw new Error('Error al enviar formulario');
      }

      const result = await response.json();
      console.log('Banda registrada:', result);
      
      setSubmitted(true);
      setFormData({
        nombre_banda: '',
        integrantes: '',
        genero: '',
        ciudad: '',
        correo_contacto: '',
        telefono_contacto: '',
        mensaje: '',
        links: '',
        press_kit: null
      });
      
    } catch (error) {
      console.error('Error al enviar formulario:', error);
      setErrors({ general: 'Error al enviar el formulario. Inténtalo de nuevo.' });
    } finally {
      setIsLoading(false);
    }
  };

  if (submitted) {
    return (
      <div className="min-h-screen bg-gray-900 text-white">
        <div className="bg-black border-b border-gray-700">
          <div className="max-w-6xl mx-auto px-4 py-6">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-green-400 rounded-full flex items-center justify-center">
                <Music className="w-6 h-6 text-white" />
              </div>
              <h1 className="text-2xl font-bold">Radio Oriente FM</h1>
            </div>
          </div>
        </div>

        <div className="max-w-4xl mx-auto px-4 py-12">
          <div className="bg-green-900/30 border border-green-700 rounded-lg p-8 text-center">
            <div className="w-16 h-16 bg-green-600 rounded-full flex items-center justify-center mx-auto mb-4">
              <Send className="w-8 h-8 text-white" />
            </div>
            <h2 className="text-2xl font-bold text-green-400 mb-4">
              ¡Formulario Enviado Exitosamente!
            </h2>
            <p className="text-gray-300 mb-6">
              Gracias por enviar tu propuesta. Nuestro equipo revisará tu información y 
              nos pondremos en contacto contigo pronto.
            </p>
            <button
              onClick={() => setSubmitted(false)}
              className="bg-red-600 hover:bg-red-700 text-white px-6 py-3 rounded-lg transition-colors"
            >
              Enviar Otra Propuesta
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <div className="bg-black border-b border-gray-700">
        <div className="max-w-6xl mx-auto px-4 py-6">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 bg-gradient-to-r from-blue-500 to-green-400 rounded-full flex items-center justify-center">
              <Music className="w-6 h-6 text-white" />
            </div>
            <h1 className="text-2xl font-bold">Radio Oriente FM</h1>
          </div>
        </div>
      </div>

      <div className="max-w-6xl mx-auto px-4 py-8">
        <div className="text-center mb-12">
          <h1 className="text-4xl font-bold mb-4 bg-gradient-to-r from-red-400 to-red-600 bg-clip-text text-transparent">
            Bandas Emergentes
          </h1>
          <p className="text-gray-300 text-lg max-w-3xl mx-auto">
            ¿Eres parte de una banda emergente? ¡Queremos conocerte! Envíanos tu información 
            y podrías ser parte de nuestra programación especial para artistas locales.
          </p>
        </div>

        <div className="grid md:grid-cols-2 gap-8">
          <div className="space-y-6">
            <div className="bg-gray-800 rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4 text-red-400">
                ¿Qué Necesitas Enviar?
              </h2>
              <ul className="space-y-3 text-gray-300">
                <li className="flex items-center gap-3">
                  <Music className="w-5 h-5 text-red-400" />
                  Información básica de la banda
                </li>
                <li className="flex items-center gap-3">
                  <Users className="w-5 h-5 text-red-400" />
                  Lista de integrantes
                </li>
                <li className="flex items-center gap-3">
                  <Link className="w-5 h-5 text-red-400" />
                  Enlaces a tu música (Spotify, YouTube, etc.)
                </li>
                <li className="flex items-center gap-3">
                  <Upload className="w-5 h-5 text-red-400" />
                  Press kit (opcional)
                </li>
              </ul>
            </div>

            <div className="bg-gray-800 rounded-lg p-6">
              <h2 className="text-xl font-bold mb-4 text-red-400">
                Proceso de Selección
              </h2>
              <div className="space-y-4 text-gray-300">
                <div className="flex gap-3">
                  <div className="w-6 h-6 bg-red-600 rounded-full flex items-center justify-center text-sm font-bold">1</div>
                  <p>Envías tu propuesta</p>
                </div>
                <div className="flex gap-3">
                  <div className="w-6 h-6 bg-red-600 rounded-full flex items-center justify-center text-sm font-bold">2</div>
                  <p>Nuestro equipo revisa tu material</p>
                </div>
                <div className="flex gap-3">
                  <div className="w-6 h-6 bg-red-600 rounded-full flex items-center justify-center text-sm font-bold">3</div>
                  <p>Te contactamos con la respuesta</p>
                </div>
              </div>
            </div>
          </div>

          <div className="bg-gray-800 rounded-lg p-6">
            <h2 className="text-xl font-bold mb-6 text-red-400">
              Envíanos tu Información
            </h2>
            
            {errors.general && (
              <div className="bg-red-900/30 border border-red-700 rounded-lg p-4 mb-6">
                <p className="text-red-400">{errors.general}</p>
              </div>
            )}

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium mb-2">
                  Nombre de la Banda *
                </label>
                <input
                  type="text"
                  name="nombre_banda"
                  value={formData.nombre_banda}
                  onChange={handleChange}
                  className={`w-full p-3 bg-gray-700 border rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500 ${
                    errors.nombre_banda ? 'border-red-500' : 'border-gray-600'
                  }`}
                  placeholder="Ej: Los Rockeros del Oriente"
                />
                {errors.nombre_banda && (
                  <p className="text-red-400 text-sm mt-1">{errors.nombre_banda}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Integrantes
                </label>
                <textarea
                  name="integrantes"
                  value={formData.integrantes}
                  onChange={handleChange}
                  rows={3}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500"
                  placeholder="Juan - Guitarra, María - Voz, Pedro - Batería..."
                />
              </div>

              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Género Musical *
                  </label>
                  <input
                    type="text"
                    name="genero"
                    value={formData.genero}
                    onChange={handleChange}
                    className={`w-full p-3 bg-gray-700 border rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500 ${
                      errors.genero ? 'border-red-500' : 'border-gray-600'
                    }`}
                    placeholder="Ej: Rock, Pop, Folk"
                  />
                  {errors.genero && (
                    <p className="text-red-400 text-sm mt-1">{errors.genero}</p>
                  )}
                </div>
                
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Ciudad
                  </label>
                  <input
                    type="text"
                    name="ciudad"
                    value={formData.ciudad}
                    onChange={handleChange}
                    className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500"
                    placeholder="Ej: Barcelona, Anzoátegui"
                  />
                </div>
              </div>

              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Correo Electrónico *
                  </label>
                  <input
                    type="email"
                    name="correo_contacto"
                    value={formData.correo_contacto}
                    onChange={handleChange}
                    className={`w-full p-3 bg-gray-700 border rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500 ${
                      errors.correo_contacto ? 'border-red-500' : 'border-gray-600'
                    }`}
                    placeholder="banda@email.com"
                  />
                  {errors.correo_contacto && (
                    <p className="text-red-400 text-sm mt-1">{errors.correo_contacto}</p>
                  )}
                </div>
                
                <div>
                  <label className="block text-sm font-medium mb-2">
                    Teléfono
                  </label>
                  <input
                    type="tel"
                    name="telefono_contacto"
                    value={formData.telefono_contacto}
                    onChange={handleChange}
                    className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500"
                    placeholder="0414-123-4567"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Enlaces a tu Música
                </label>
                <textarea
                  name="links"
                  value={formData.links}
                  onChange={handleChange}
                  rows={3}
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500"
                  placeholder="Spotify: https://open.spotify.com/...&#10;YouTube: https://youtube.com/...&#10;Instagram: @tu_banda"
                />
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Mensaje *
                </label>
                <textarea
                  name="mensaje"
                  value={formData.mensaje}
                  onChange={handleChange}
                  rows={4}
                  className={`w-full p-3 bg-gray-700 border rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500 ${
                    errors.mensaje ? 'border-red-500' : 'border-gray-600'
                  }`}
                  placeholder="Cuéntanos sobre tu banda, tu música, tus objetivos..."
                />
                {errors.mensaje && (
                  <p className="text-red-400 text-sm mt-1">{errors.mensaje}</p>
                )}
              </div>

              <div>
                <label className="block text-sm font-medium mb-2">
                  Press Kit (PDF, DOC, ZIP - Opcional)
                </label>
                <input
                  type="file"
                  name="press_kit"
                  onChange={handleFileChange}
                  accept=".pdf,.doc,.docx,.zip,.rar"
                  className="w-full p-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-red-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:bg-red-600 file:text-white file:cursor-pointer hover:file:bg-red-700"
                />
                <p className="text-gray-400 text-sm mt-1">
                  Incluye fotos, biografía, lista de canciones, etc.
                </p>
              </div>

              <button
                type="button"
                onClick={handleSubmit}
                disabled={isLoading}
                className={`w-full p-4 rounded-lg font-semibold transition-all ${
                  isLoading 
                    ? 'bg-gray-600 cursor-not-allowed' 
                    : 'bg-red-600 hover:bg-red-700 hover:shadow-lg hover:shadow-red-600/25'
                }`}
              >
                {isLoading ? (
                  <div className="flex items-center justify-center gap-2">
                    <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin" />
                    Enviando...
                  </div>
                ) : (
                  <div className="flex items-center justify-center gap-2">
                    <Send className="w-5 h-5" />
                    Enviar Propuesta
                  </div>
                )}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Emergente;
