import React, { useContext, useEffect, useState } from "react";
import { Play, Pause, Volume2, VolumeX } from "lucide-react";
import { AudioContextGlobal } from "../contexts/AudioContext";
import './RadioPlayer.css'; // Puedes crear un CSS distinto o usar el mismo que RadioPlayer

const Reproductor = () => {
  const { audioRef, isPlaying, togglePlay, volume, setVolume, toggleMute, streamUrl } = useContext(AudioContextGlobal);
  const [isMuted, setIsMuted] = useState(volume === 0);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));

  // Sincronizar mute con volumen
  useEffect(() => {
    setIsMuted(volume === 0);
  }, [volume]);

  // Reloj en tiempo real
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleVolumeChange = (e) => {
    setVolume(Number(e.target.value));
  };

  const handleToggleMute = () => {
    toggleMute();
  };

  return (
    <div className="reproductor-wrapper">
      <h1>Radio Oriente FM - 107.1</h1>

      <div className="reproductor-main">
        <button onClick={togglePlay} className="control-btn" disabled={!streamUrl}>
          {isPlaying ? <Pause size={40} /> : <Play size={40} />}
        </button>

        <span className="current-time">{currentTime}</span>

        <div className="volume-controls">
          <button onClick={handleToggleMute} className="control-btn">
            {isMuted ? <VolumeX size={30} /> : <Volume2 size={30} />}
          </button>

          <input
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={handleVolumeChange}
            className="volume-range"
          />
        </div>
      </div>

      <p>{streamUrl ? "En vivo" : "Cargando stream..."}</p>
    </div>
  );
};

export default Reproductor;
