import React from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const FondoParticulas = () => {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        fullScreen: { enable: true },

        fpsLimit: 60,
        interactivity: {
          events: {
            onHover: {
              enable: true,
              mode: ["attract"]
            },
            onClick: {
              enable: true,
              mode: "push"
            }
          },
          modes: {
            attract: {
              distance: 200,
              duration: 0.8, // más lento
              factor: 1.2 // menos brusco
            },
            push: {
              quantity: 2
            }
          }
        },
        particles: {
          color: {
            value: ["#dc2626", "#b91c1c", "#991b1b", "#7f1d1d", "#ffffff"]
          },
          links: {
            enable: true,
            color: "#dc2626",
            distance: 150,
            opacity: 0.4,
            width: 1
          },
          collisions: { enable: true },
          move: {
            enable: true,
            speed: 1.2, // más lento por defecto
            direction: "none",
            random: false, // ❌ quitamos cambios bruscos
            straight: false,
            outModes: { default: "bounce" }
          },
          number: {
            value: 120,
            density: {
              enable: true,
              area: 800
            }
          },
          opacity: {
            value: 0.6,
            random: false, // ❌ quitamos parpadeos bruscos
            animation: {
              enable: true,
              speed: 0.3,
              minimumValue: 0.4,
              sync: false
            }
          },
          shape: {
            type: ["circle", "triangle", "square"]
          },
          size: {
            value: { min: 3, max: 6 },
            random: false, // ❌ quitamos saltos bruscos
            animation: {
              enable: true,
              speed: 0.4,
              minimumValue: 2,
              sync: false
            }
          }
        },
        detectRetina: true
      }}
    />
  );
};

export default FondoParticulas;
