import React, { useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { AuthProvider } from './contexts/AuthContext';
import './App.css';

// Páginas
import Home from './paginas/Home';
import Programacion from './paginas/Programacion';
import Noticias from './paginas/Noticias';
import Contacto from './paginas/Contacto';
import Suscripcion from './paginas/Suscripcion';
import Blog from './paginas/Blog';
import Login from './paginas/Login';
import Registro from './paginas/Registro';
import Emergente from './componentes/Emergente';
import Reproductor from './paginas/Reproductor';

// Layouts en español
import LayoutPrincipal from './layouts/LayoutPrincipal';
import LayoutPantallaCompleta from './layouts/LayoutPantallaCompleta';

function App() {

  useEffect(() => {
    const originalTitle = document.title;

    const handleVisibilityChange = () => {
      document.title = document.hidden ? "¡Vuelve a la mejor radio!" : originalTitle;
    };

    document.addEventListener("visibilitychange", handleVisibilityChange);

    return () => {
      document.removeEventListener("visibilitychange", handleVisibilityChange);
    };
  }, []);

  return (
    <AuthProvider>
      <Router>
        <Routes>

          {/* Layout principal con navbar, radio, chat y footer */}
          <Route element={<LayoutPrincipal />}>
            <Route path="/" element={<Home />} />
            <Route path="/programacion" element={<Programacion />} />
            <Route path="/noticias" element={<Noticias />} />
            <Route path="/contacto" element={<Contacto />} />
            <Route path="/suscripcion" element={<Suscripcion />} />
            <Route path="/blog" element={<Blog />} />
            <Route path="/emergente" element={<Emergente />} />
            <Route path="/login" element={<Login />} />
            <Route path="/registro" element={<Registro />} />
          </Route>

          {/* Layout full screen solo para el reproductor */}
          <Route element={<LayoutPantallaCompleta />}>
            <Route path="/reproductor" element={<Reproductor />} />
          </Route>

        </Routes>

        <Toaster 
          position="top-right"
          toastOptions={{
            duration: 4000,
            style: {
              background: '#000',
              color: '#fff',
            },
          }}
        />
      </Router>
    </AuthProvider>
  );
}

export default App;
