import React, { useState, useEffect, useContext, useRef } from "react";
import { Play, Pause, Volume2, VolumeX, ChevronDown, ChevronUp, Layout } from "lucide-react";
import { AudioContextGlobal } from "../contexts/AudioContext";
import { useNavigate } from "react-router-dom";
import gsap from "gsap";
import './RadioPlayer.css';

const RadioPlayer = () => {
  const { isPlaying, togglePlay, volume, setVolume, streamUrl } = useContext(AudioContextGlobal);
  const navigate = useNavigate();
  const [lastVolume, setLastVolume] = useState(volume);
  const [isMuted, setIsMuted] = useState(volume === 0);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
  const [isCollapsed, setIsCollapsed] = useState(false);
  const fullPlayerBtnRef = useRef(null);

  // Actualiza mute según volumen
  useEffect(() => {
    setIsMuted(volume === 0);
    if (volume > 0) setLastVolume(volume);
  }, [volume]);

  // Reloj en tiempo real
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Animación GSAP para el botón de reproductor completo
  useEffect(() => {
    if (fullPlayerBtnRef.current) {
      gsap.fromTo(fullPlayerBtnRef.current, 
        { scale: 0.8, opacity: 0 }, 
        { scale: 1, opacity: 1, duration: 0.5, ease: "back.out(1.7)" }
      );
    }
  }, []);

  const handleVolumeChange = (e) => {
    const newVol = Number(e.target.value);
    setVolume(newVol);
  };

  const handleToggleMute = () => {
    if (isMuted) setVolume(lastVolume || 1);
    else setVolume(0);
  };

  const toggleCollapse = () => setIsCollapsed(prev => !prev);

  return (
    <div className={`radio-player-wrapper ${isCollapsed ? 'collapsed' : ''}`}>
      {/* Botón de colapso */}
      <div className="collapse-btn-wrapper">
        <button 
          className="collapse-btn" 
          onClick={toggleCollapse}
          aria-label={isCollapsed ? "Mostrar reproductor" : "Ocultar reproductor"}
        >
          {isCollapsed ? <ChevronUp size={20}/> : <ChevronDown size={20}/>}
        </button>
      </div>

      {/* Reproductor principal */}
      <div className={`radio-player ${isPlaying ? 'playing' : 'paused'}`}>
        <div className="gradient-left" />
        <div className="gradient-right" />

        {/* Información de radio */}
        <div className="radio-info">
          <span
            className={`status-indicator ${isPlaying ? 'playing' : 'paused'}`}
            aria-label={isPlaying ? "En transmisión" : "No está transmitiendo"}
            title={isPlaying ? "On Air" : "Off Air"}
          />
          <div className="radio-details">
            <span className="radio-name">Radio Oriente FM</span>
            <span className="radio-frequency">107.1 FM - En vivo</span>
          </div>
        </div>

        {/* Controles centrales */}
        <div className="player-controls">
          <button
            onClick={togglePlay}
            aria-label="Play/Pause"
            className="control-icon"
            disabled={!streamUrl}
          >
            {isPlaying ? <Pause size={20} /> : <Play size={20} />}
          </button>

          {/* Botón abrir reproductor completo */}
          <button
            ref={fullPlayerBtnRef}
            className="btn-full-player-icon"
            onClick={() => navigate("/reproductor")}
            aria-label="Abrir reproductor completo"
          >
            <Layout size={20} />
          </button>

          <span className="current-time">{currentTime}</span>
        </div>

        {/* Controles de volumen */}
        <div className="volume-controls">
          <button
            onClick={handleToggleMute}
            aria-label="Mute/Unmute"
            className="control-button"
          >
            {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
          </button>

          <div className="volume-slider-container">
            <div 
              className={`volume-progress ${isPlaying ? 'playing' : ''}`}
              style={{ width: `${volume * 100}%` }}
            />
            <div 
              className="volume-thumb"
              style={{ left: `calc(${volume * 100}% - 7px)` }}
            />
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={handleVolumeChange}
              className="volume-range"
              aria-label="Volume control"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default RadioPlayer;
