// FondoParticulas.js
import React from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const FondoParticulas = () => {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  const particlesLoaded = (container) => {
    // opcional: puedes usarlo para debug
    console.log(container);
  };

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      loaded={particlesLoaded}
      options={{
        fullScreen: { enable: true },
        background: { color: "#0A1F2B" },
        fpsLimit: 60,
        interactivity: {
          events: {
            onHover: { enable: true, mode: "repulse" },
            onClick: { enable: true, mode: "push" },
            resize: true,
          },
          modes: {
            repulse: { distance: 100, duration: 0.4 },
            push: { quantity: 4 },
          },
        },
        particles: {
          color: { value: "#dc2626" },
          links: { color: "#ffffff", distance: 120, enable: true, opacity: 0.2, width: 1 },
          collisions: { enable: false },
          move: { enable: true, speed: 1, outModes: { default: "bounce" }, random: true },
          number: { value: 60, density: { enable: true, area: 800 } },
          opacity: { value: 0.5 },
          shape: { type: "circle" },
          size: { value: { min: 2, max: 5 } },
        },
        detectRetina: true,
      }}
    />
  );
};

export default FondoParticulas;
