import React from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

const FondoParticulas = () => {
  const particlesInit = async (main) => {
    await loadFull(main);
  };

  return (
    <Particles
      id="tsparticles"
      init={particlesInit}
      options={{
        fullScreen: { enable: true },
        fpsLimit: 120,
        interactivity: {
          events: {
            onHover: {
              enable: true,
              mode: ["attract", "bubble"]
            },
            onClick: {
              enable: true,
              mode: "push"
            }
          },
          modes: {
            attract: {
              distance: 200,
              duration: 0.4,
              factor: 3
            },
            bubble: {
              distance: 250,
              size: 8,
              duration: 2,
              opacity: 0.8
            },
            push: {
              quantity: 3
            }
          }
        },
        particles: {
          color: {
            value: ["#dc2626", "#b91c1c", "#991b1b", "#7f1d1d", "#ffffff"]
          },
          links: {
            enable: true,
            color: "#dc2626",
            distance: 150,
            opacity: 0.6,
            width: 1
          },
          collisions: {
            enable: true
          },
          move: {
            enable: true,
            speed: 1.2, // mÃ¡s lento y suave
            direction: "none",
            random: false, // ðŸš« no cambios abruptos
            straight: false,
            outModes: {
              default: "bounce"
            },
            attract: {
              enable: false,
              rotateX: 600,
              rotateY: 1200
            }
          },
          number: {
            value: 150,
            density: {
              enable: true,
              area: 600
            }
          },
          opacity: {
            value: 0.7,
            random: false, // ðŸš« no saltos de opacidad
            animation: {
              enable: true,
              speed: 0.3, // animaciÃ³n lenta y fluida
              minimumValue: 0.4,
              sync: false
            }
          },
          shape: {
            type: ["circle", "triangle", "square"]
          },
          size: {
            value: 4,
            random: false, // ðŸš« sin cambios bruscos de tamaÃ±o
            animation: {
              enable: true,
              speed: 0.5, // animaciÃ³n lenta
              minimumValue: 2,
              sync: false
            }
          }
        },
        detectRetina: true
        // âŒ eliminamos emitters para que no exploten partÃ­culas
      }}
    />
  );
};

export default FondoParticulas;
