import React, { useState } from 'react';
import { Upload, Music, Users, Link, Send } from 'lucide-react';

const Emergente = () => {
  const [formData, setFormData] = useState({
    nombre_banda: '',
    integrantes: '',
    genero: '',
    ciudad: '',
    correo_contacto: '',
    telefono_contacto: '',
    mensaje: '',
    links: '',
    press_kit: null
  });

  const [isLoading, setIsLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setFormData(prev => ({
      ...prev,
      press_kit: file
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.nombre_banda.trim()) {
      newErrors.nombre_banda = 'El nombre de la banda es requerido';
    }
    
    if (!formData.genero.trim()) {
      newErrors.genero = 'El género musical es requerido';
    }
    
    if (!formData.correo_contacto.trim()) {
      newErrors.correo_contacto = 'El correo electrónico es requerido';
    } else if (!/\S+@\S+\.\S+/.test(formData.correo_contacto)) {
      newErrors.correo_contacto = 'Por favor ingresa un correo electrónico válido';
    }
    
    if (!formData.mensaje.trim()) {
      newErrors.mensaje = 'El mensaje es requerido';
    }

    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const newErrors = validateForm();
    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setIsLoading(true);
    
    try {
      const formDataToSend = new FormData();
      
      Object.keys(formData).forEach(key => {
        if (formData[key] !== null && formData[key] !== '') {
          formDataToSend.append(key, formData[key]);
        }
      });

      const response = await fetch('/api/emergentes/', {
        method: 'POST',
        body: formDataToSend,
      });
      
      if (!response.ok) {
        throw new Error('Error al enviar formulario');
      }

      const result = await response.json();
      console.log('Banda registrada:', result);
      
      setSubmitted(true);
      setFormData({
        nombre_banda: '',
        integrantes: '',
        genero: '',
        ciudad: '',
        correo_contacto: '',
        telefono_contacto: '',
        mensaje: '',
        links: '',
        press_kit: null
      });
      
    } catch (error) {
      console.error('Error al enviar formulario:', error);
      setErrors({ general: 'Error al enviar el formulario. Inténtalo de nuevo.' });
    } finally {
      setIsLoading(false);
    }
  };

  if (submitted) {
    return (
      <div style={{ minHeight: '100vh', padding: '2rem', background: 'linear-gradient(135deg, #e3f2fd, #f3e5f5)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <div className="card" style={{ maxWidth: '600px', textAlign: 'center' }}>
          <div style={{ marginBottom: '2rem' }}>
            <div style={{ 
              width: '64px', 
              height: '64px', 
              background: '#4caf50', 
              borderRadius: '50%', 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center',
              margin: '0 auto 1rem'
            }}>
              <Send style={{ width: '32px', height: '32px', color: 'white' }} />
            </div>
            <h2 style={{ color: '#4caf50', marginBottom: '1rem', fontSize: '1.5rem', fontWeight: 'bold' }}>
              ¡Formulario Enviado Exitosamente!
            </h2>
            <p style={{ color: '#666', marginBottom: '2rem' }}>
              Gracias por enviar tu propuesta. Nuestro equipo revisará tu información y 
              nos pondremos en contacto contigo pronto.
            </p>
            <button
              onClick={() => setSubmitted(false)}
              style={{
                backgroundColor: '#dc2626',
                color: 'white',
                padding: '0.75rem 1.5rem',
                borderRadius: '0.5rem',
                border: 'none',
                cursor: 'pointer',
                fontSize: '1rem',
                transition: 'background-color 0.2s'
              }}
              onMouseOver={(e) => e.target.style.backgroundColor = '#b91c1c'}
              onMouseOut={(e) => e.target.style.backgroundColor = '#dc2626'}
            >
              Enviar Otra Propuesta
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div style={{ minHeight: '100vh', padding: '2rem', background: 'linear-gradient(135deg, #e3f2fd, #f3e5f5)' }}>
      <div style={{ maxWidth: '1200px', margin: '0 auto' }}>
        {/* Header */}
        <div style={{ textAlign: 'center', marginBottom: '3rem' }}>
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '1rem', marginBottom: '2rem' }}>
            <div style={{ 
              width: '40px', 
              height: '40px', 
              background: 'linear-gradient(135deg, #e80c0cff, #011107ff)', 
              borderRadius: '50%', 
              display: 'flex', 
              alignItems: 'center', 
              justifyContent: 'center'
            }}>
              <Music style={{ width: '24px', height: '24px', color: 'white' }} />
            </div>
            <h1 style={{ fontSize: '2rem', fontWeight: 'bold', color: '#1f2937' }}>Radio Oriente FM</h1>
          </div>
          <h2 style={{ 
            fontSize: '2.5rem', 
            fontWeight: 'bold', 
            marginBottom: '1rem',
            background: 'linear-gradient(135deg, #dc2626, #ef4444)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            backgroundClip: 'text'
          }}>
            Bandas Emergentes
          </h2>
          <p style={{ color: '#666', fontSize: '1.125rem', maxWidth: '800px', margin: '0 auto' }}>
            ¿Eres parte de una banda emergente? ¡Queremos conocerte! Envíanos tu información 
            y podrías ser parte de nuestra programación especial para artistas locales.
          </p>
        </div>

        <div className="grid grid-cols-2">
          {/* Información */}
          <div style={{ paddingRight: '1rem' }}>
            <div className="card" style={{ marginBottom: '1.5rem' }}>
              <div className="card-header">
                <h3 className="card-title" style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                  <Music style={{ width: '20px', height: '20px', color: '#dc2626' }} />
                  ¿Qué Necesitas Enviar?
                </h3>
              </div>
              <ul style={{ listStyle: 'none', padding: 0 }}>
                <li style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '0.75rem', color: '#666' }}>
                  <Music style={{ width: '16px', height: '16px', color: '#dc2626' }} />
                  Información básica de la banda
                </li>
                <li style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '0.75rem', color: '#666' }}>
                  <Users style={{ width: '16px', height: '16px', color: '#dc2626' }} />
                  Lista de integrantes
                </li>
                <li style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '0.75rem', color: '#666' }}>
                  <Link style={{ width: '16px', height: '16px', color: '#dc2626' }} />
                  Enlaces a tu música (Spotify, YouTube, etc.)
                </li>
                <li style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '0.75rem', color: '#666' }}>
                  <Upload style={{ width: '16px', height: '16px', color: '#dc2626' }} />
                  Press kit (opcional)
                </li>
              </ul>
            </div>

            <div className="card">
              <div className="card-header">
                <h3 className="card-title">Proceso de Selección</h3>
              </div>
              <div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '1rem' }}>
                  <div style={{ 
                    width: '24px', 
                    height: '24px', 
                    backgroundColor: '#dc2626', 
                    borderRadius: '50%', 
                    display: 'flex', 
                    alignItems: 'center', 
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: '0.875rem',
                    fontWeight: 'bold'
                  }}>1</div>
                  <p style={{ margin: 0, color: '#666' }}>Envías tu propuesta</p>
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem', marginBottom: '1rem' }}>
                  <div style={{ 
                    width: '24px', 
                    height: '24px', 
                    backgroundColor: '#dc2626', 
                    borderRadius: '50%', 
                    display: 'flex', 
                    alignItems: 'center', 
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: '0.875rem',
                    fontWeight: 'bold'
                  }}>2</div>
                  <p style={{ margin: 0, color: '#666' }}>Nuestro equipo revisa tu material</p>
                </div>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
                  <div style={{ 
                    width: '24px', 
                    height: '24px', 
                    backgroundColor: '#dc2626', 
                    borderRadius: '50%', 
                    display: 'flex', 
                    alignItems: 'center', 
                    justifyContent: 'center',
                    color: 'white',
                    fontSize: '0.875rem',
                    fontWeight: 'bold'
                  }}>3</div>
                  <p style={{ margin: 0, color: '#666' }}>Te contactamos con la respuesta</p>
                </div>
              </div>
            </div>
          </div>

          {/* Formulario */}
          <div style={{ paddingLeft: '1rem' }}>
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">Envíanos tu Información</h3>
              </div>
              
              {errors.general && (
                <div style={{
                  backgroundColor: '#fef2f2',
                  border: '1px solid #fca5a5',
                  borderRadius: '0.5rem',
                  padding: '1rem',
                  marginBottom: '1.5rem'
                }}>
                  <p style={{ color: '#dc2626', margin: 0 }}>{errors.general}</p>
                </div>
              )}

              <div>
                <div className="form-group">
                  <label className="form-label">
                    Nombre de la Banda *
                  </label>
                  <input
                    type="text"
                    name="nombre_banda"
                    value={formData.nombre_banda}
                    onChange={handleChange}
                    className={`form-input ${errors.nombre_banda ? 'error' : ''}`}
                    style={errors.nombre_banda ? { borderColor: '#dc2626' } : {}}
                    placeholder="Ej: Los Rockeros del Oriente"
                  />
                  {errors.nombre_banda && (
                    <p style={{ color: '#dc2626', fontSize: '0.875rem', marginTop: '0.25rem' }}>
                      {errors.nombre_banda}
                    </p>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">
                    Integrantes
                  </label>
                  <textarea
                    name="integrantes"
                    value={formData.integrantes}
                    onChange={handleChange}
                    className="form-textarea"
                    placeholder="Juan - Guitarra, María - Voz, Pedro - Batería..."
                  />
                </div>

                <div className="grid grid-cols-2">
                  <div className="form-group" style={{ marginRight: '0.75rem' }}>
                    <label className="form-label">
                      Género Musical *
                    </label>
                    <input
                      type="text"
                      name="genero"
                      value={formData.genero}
                      onChange={handleChange}
                      className="form-input"
                      style={errors.genero ? { borderColor: '#dc2626' } : {}}
                      placeholder="Ej: Rock, Pop, Folk"
                    />
                    {errors.genero && (
                      <p style={{ color: '#dc2626', fontSize: '0.875rem', marginTop: '0.25rem' }}>
                        {errors.genero}
                      </p>
                    )}
                  </div>
                  
                  <div className="form-group" style={{ marginLeft: '0.75rem' }}>
                    <label className="form-label">
                      Ciudad
                    </label>
                    <input
                      type="text"
                      name="ciudad"
                      value={formData.ciudad}
                      onChange={handleChange}
                      className="form-input"
                      placeholder="Ej: Barcelona, Anzoátegui"
                    />
                  </div>
                </div>

                <div className="grid grid-cols-2">
                  <div className="form-group" style={{ marginRight: '0.75rem' }}>
                    <label className="form-label">
                      Correo Electrónico *
                    </label>
                    <input
                      type="email"
                      name="correo_contacto"
                      value={formData.correo_contacto}
                      onChange={handleChange}
                      className="form-input"
                      style={errors.correo_contacto ? { borderColor: '#dc2626' } : {}}
                      placeholder="banda@email.com"
                    />
                    {errors.correo_contacto && (
                      <p style={{ color: '#dc2626', fontSize: '0.875rem', marginTop: '0.25rem' }}>
                        {errors.correo_contacto}
                      </p>
                    )}
                  </div>
                  
                  <div className="form-group" style={{ marginLeft: '0.75rem' }}>
                    <label className="form-label">
                      Teléfono
                    </label>
                    <input
                      type="tel"
                      name="telefono_contacto"
                      value={formData.telefono_contacto}
                      onChange={handleChange}
                      className="form-input"
                      placeholder="0414-123-4567"
                    />
                  </div>
                </div>

                <div className="form-group">
                  <label className="form-label">
                    Enlaces a tu Música
                  </label>
                  <textarea
                    name="links"
                    value={formData.links}
                    onChange={handleChange}
                    className="form-textarea"
                    placeholder="Spotify: https://open.spotify.com/...&#10;YouTube: https://youtube.com/...&#10;Instagram: @tu_banda"
                  />
                </div>

                <div className="form-group">
                  <label className="form-label">
                    Mensaje *
                  </label>
                  <textarea
                    name="mensaje"
                    value={formData.mensaje}
                    onChange={handleChange}
                    className="form-textarea"
                    style={errors.mensaje ? { borderColor: '#dc2626' } : {}}
                    placeholder="Cuéntanos sobre tu banda, tu música, tus objetivos..."
                  />
                  {errors.mensaje && (
                    <p style={{ color: '#dc2626', fontSize: '0.875rem', marginTop: '0.25rem' }}>
                      {errors.mensaje}
                    </p>
                  )}
                </div>

                <div className="form-group">
                  <label className="form-label">
                    Press Kit (PDF, DOC, ZIP - Opcional)
                  </label>
                  <input
                    type="file"
                    name="press_kit"
                    onChange={handleFileChange}
                    accept=".pdf,.doc,.docx,.zip,.rar"
                    className="form-input"
                    style={{ cursor: 'pointer' }}
                  />
                  <p style={{ color: '#666', fontSize: '0.875rem', marginTop: '0.25rem' }}>
                    Incluye fotos, biografía, lista de canciones, etc.
                  </p>
                </div>

                <button
                  type="button"
                  onClick={handleSubmit}
                  disabled={isLoading}
                  style={{
                    width: '100%',
                    padding: '1rem',
                    backgroundColor: isLoading ? '#9ca3af' : '#dc2626',
                    color: 'white',
                    border: 'none',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    fontWeight: '600',
                    cursor: isLoading ? 'not-allowed' : 'pointer',
                    transition: 'all 0.2s',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    gap: '0.5rem'
                  }}
                  onMouseOver={(e) => {
                    if (!isLoading) {
                      e.target.style.backgroundColor = '#b91c1c';
                      e.target.style.boxShadow = '0 10px 15px -3px rgba(220, 38, 38, 0.25)';
                    }
                  }}
                  onMouseOut={(e) => {
                    if (!isLoading) {
                      e.target.style.backgroundColor = '#dc2626';
                      e.target.style.boxShadow = 'none';
                    }
                  }}
                >
                  {isLoading ? (
                    <>
                      <div style={{
                        width: '20px',
                        height: '20px',
                        border: '2px solid white',
                        borderTop: '2px solid transparent',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite'
                      }} />
                      Enviando...
                    </>
                  ) : (
                    <>
                      <Send style={{ width: '20px', height: '20px' }} />
                      Enviar Propuesta
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      <style>
        {`
          @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
          }
        `}
      </style>
    </div>
  );
};

export default Emergente;
