import React, { useCallback, useMemo } from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

/**
 * FondoParticulas: componente memoizado que no se re-inicializa en cada render.
 * - useCallback para particlesInit (stable)
 * - useMemo para options (stable)
 * - export default React.memo(...) evita re-renders si no cambian props
 */
const FondoParticulasComponent = () => {
  const particlesInit = useCallback(async (engine) => {
    // Carga todas las funciones necesarias de tsparticles una sola vez
    await loadFull(engine);
  }, []);

  // Opciones memorizadas para evitar recrearlas en cada render del padre
  const options = useMemo(() => ({
    fullScreen: {
      enable: true,
      zIndex: -2 // canvas detrás del contenido
    },
    background: {
      color: { value: "transparent" } // fondo transparente para aplicar gradiente por CSS
    },
    fpsLimit: 60,
    interactivity: {
      detectsOn: "canvas", // detectar sólo cuando el cursor está sobre el canvas (pero canvas estará detrás)
      events: {
        onHover: {
          enable: false, // desactivado para evitar que hover sobre controles afecte (puedes true si quieres hover)
          mode: "repulse"
        },
        onClick: {
          enable: false, // desactivado para evitar "explosiones" al click en UI
          mode: "push"
        },
        resize: true
      },
      modes: {
        repulse: { distance: 120, duration: 0.6 },
        bubble: { distance: 200, size: 8, duration: 1 },
        push: { quantity: 2 }
      }
    },
    particles: {
      color: {
        value: ["#dc2626", "#b91c1c", "#991b1b", "#7f1d1d", "#ffffff"]
      },
      links: {
        enable: true,
        color: "#dc2626",
        distance: 140,
        opacity: 0.25,
        width: 1
      },
      collisions: { enable: false },
      move: {
        enable: true,
        speed: 0.6,     // lento y fluido por defecto
        direction: "none",
        random: false,  // evita saltos
        straight: false,
        outModes: { default: "bounce" }
      },
      number: {
        value: 110,
        density: { enable: true, area: 700 }
      },
      opacity: {
        value: 0.7,
        random: false,
        animation: {
          enable: true,
          speed: 0.35,   // animación suave
          minimumValue: 0.4,
          sync: false
        }
      },
      shape: {
        type: ["circle", "triangle", "square"]
      },
      size: {
        value: { min: 2, max: 5 },
        random: false,
        animation: {
          enable: true,
          speed: 0.7,
          minimumValue: 2,
          sync: false
        }
      }
    },
    detectRetina: true
  }), []);

  return <Particles id="tsparticles" init={particlesInit} options={options} />;
};

const FondoParticulas = React.memo(FondoParticulasComponent);
export default FondoParticulas;
