import React, { useContext } from "react";
import { Play, Pause, Volume2, VolumeX } from "lucide-react";
import { AudioContextGlobal } from "../contexts/AudioContext";
import './reproductor.css';

const Reproductor = () => {
  const { isPlaying, togglePlay, volume, setVolume, toggleMute, streamUrl } = useContext(AudioContextGlobal);

  return (
    <div className="reproductor-page h-screen w-screen flex flex-col items-center justify-center relative overflow-hidden text-white">
      <div className="absolute inset-0 bg-[url('/images/fondo-agua.jpg')] bg-cover bg-center opacity-30"></div>
      <div className="z-10 flex flex-col items-center">
        <img src="/images/logo.png" alt="Radio Oriente" className="w-64 h-64 rounded-2xl shadow-2xl object-cover"/>
        <div className="flex items-center gap-6 mt-8">
          <button onClick={togglePlay} className="w-20 h-20 rounded-full bg-white text-blue-600 flex items-center justify-center shadow-lg hover:scale-105 transition">
            {isPlaying ? <Pause size={40}/> : <Play size={40}/>}
          </button>
        </div>
        <div className="mt-6 flex items-center gap-4">
          <button onClick={toggleMute}>{volume === 0 ? <VolumeX size={24}/> : <Volume2 size={24}/>}</button>
          <input type="range" min="0" max="1" step="0.01" value={volume} onChange={(e) => setVolume(Number(e.target.value))} className="w-48"/>
        </div>
      </div>
    </div>
  );
};

export default Reproductor;
