import React, { useState, useEffect, useContext, useRef } from "react";
import { Play, Pause, Volume2, VolumeX, ChevronDown, ChevronUp, Maximize } from "lucide-react";
import { AudioContextGlobal } from "../contexts/AudioContext";
import { useNavigate } from "react-router-dom";
import { gsap } from "gsap";
import './RadioPlayer.css';

const RadioPlayer = () => {
  const { isPlaying, togglePlay, volume, setVolume, streamUrl } = useContext(AudioContextGlobal);
  const navigate = useNavigate();
  const fullBtnRef = useRef(null);

  const [lastVolume, setLastVolume] = useState(volume);
  const [isMuted, setIsMuted] = useState(volume === 0);
  const [currentTime, setCurrentTime] = useState(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
  const [isCollapsed, setIsCollapsed] = useState(false);

  useEffect(() => {
    setIsMuted(volume === 0);
    if (volume > 0) setLastVolume(volume);
  }, [volume]);

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentTime(new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" }));
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleVolumeChange = (e) => setVolume(Number(e.target.value));
  const handleToggleMute = () => setVolume(isMuted ? (lastVolume || 1) : 0);
  const toggleCollapse = () => setIsCollapsed(prev => !prev);

  // Animación GSAP en hover del botón
  useEffect(() => {
    const btn = fullBtnRef.current;
    if (!btn) return;

    const hoverAnim = gsap.to(btn, {
      scale: 1.2,
      duration: 0.2,
      paused: true,
      ease: "power1.inOut",
    });

    const handleMouseEnter = () => hoverAnim.play();
    const handleMouseLeave = () => hoverAnim.reverse();

    btn.addEventListener("mouseenter", handleMouseEnter);
    btn.addEventListener("mouseleave", handleMouseLeave);

    return () => {
      btn.removeEventListener("mouseenter", handleMouseEnter);
      btn.removeEventListener("mouseleave", handleMouseLeave);
    };
  }, []);

  return (
    <div className={`radio-player-wrapper ${isCollapsed ? 'collapsed' : ''}`}>
      <div className="collapse-btn-wrapper">
        <button 
          className="collapse-btn" 
          onClick={toggleCollapse}
          aria-label={isCollapsed ? "Mostrar reproductor" : "Ocultar reproductor"}
        >
          {isCollapsed ? <ChevronUp size={20}/> : <ChevronDown size={20}/>}
        </button>
      </div>

      <div className={`radio-player ${isPlaying ? 'playing' : 'paused'}`}>
        <div className="gradient-left" />
        <div className="gradient-right" />

        <div className="radio-info">
          <span className={`status-indicator ${isPlaying ? 'playing' : 'paused'}`} />
          <div className="radio-details">
            <span className="radio-name">Radio Oriente FM</span>
            <span className="radio-frequency">107.1 FM - En vivo</span>
          </div>
        </div>

        <div className="player-controls">
          <button
            onClick={togglePlay}
            aria-label="Play/Pause"
            className="control-icon"
            disabled={!streamUrl}
          >
            {isPlaying ? <Pause size={20} /> : <Play size={20} />}
          </button>

          <span className="current-time">{currentTime}</span>

          {/* Botón GSAP / Nav-icon */}
          <button
            ref={fullBtnRef}
            className="btn-full-player-gsap"
            onClick={() => navigate("/reproductor")}
            aria-label="Abrir reproductor completo"
          >
            <Maximize size={18} />
          </button>
        </div>

        <div className="volume-controls">
          <button onClick={handleToggleMute} className="control-button">
            {isMuted ? <VolumeX size={20} /> : <Volume2 size={20} />}
          </button>

          <div className="volume-slider-container">
            <div className={`volume-progress ${isPlaying ? 'playing' : ''}`} style={{ width: `${volume * 100}%` }} />
            <div className="volume-thumb" style={{ left: `calc(${volume * 100}% - 7px)` }} />
            <input type="range" min="0" max="1" step="0.01" value={volume} onChange={handleVolumeChange} className="volume-range" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default RadioPlayer;
